export function getOwnedPokemon() {
  var data = localStorage.getItem('owned_pokemon');

  if (typeof data == 'string') {
    return JSON.parse(data);
  } else {
    localStorage.setItem('owned_pokemon', '[]');
    return [];
  }
}
export function addOwnedPokemon(payload) {
  var data = getOwnedPokemon();
  if (data.find(function (t) {
    return t.name == payload.name;
  })) throw 'Nickname Already Exists!';
  data.push(payload);
  localStorage.setItem('owned_pokemon', JSON.stringify(data));
}
